import React, { useState, useEffect } from "react";
import "../App.css";
import axios from "axios";

const Register = () => {
  const [valid, setValid] = useState(1);
  const [email, setEmail] = useState("");
  const [failed, setFailed] = useState(0);
  const [username, setUsername] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  const isValidEmail = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g;

  const validateEmail = (e: any) => {
    if (e.target?.value && e.target.value.match(isValidEmail)) {
      setEmail(e.target.value);
      setValid(1);
    } else {
      setValid(0);
    }
  };

  const handleRegister = () => {
    console.log(email, password, password2, phone, username, failed);
    if (password !== password2) {
      setFailed(1);
    } else {
      setFailed(0);
    }
    if (!email || !username || !phone || !password || !password2) {
      alert("All fields are required");
    }
    return;
  };
  return (
    <div className="align-items-center d-flex align-items-center justify-content-center text-center">
      <div className="border p-5 rounded align-items-center container container-fluid">
        <div>
          <h1>Register below</h1>
        </div>
        <div>
          <form action="">
            <div className="form-floating mb-3">
              <input
                type="email"
                className="form-control"
                id="email"
                placeholder="name@example.com"
                onChange={validateEmail}
                required
              />
              <label htmlFor="email">Email address</label>
              {valid ? (
                <div className="d-flex justify-self-start text-danger">
                  Passwords not the same.
                </div>
              ) : (
                ""
              )}
            </div>
            <div className="form-floating mb-3">
              <input
                type="number"
                className="form-control"
                id="phone"
                placeholder="Phone"
                onChange={(e) => setPhone(e.target.value)}
              />
              <label htmlFor="phone">Phone</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id="username"
                placeholder="Username"
                onChange={(e) => setUsername(e.target.value)}
              />
              <label htmlFor="username">Username</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id="password"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
              />
              <label htmlFor="password">Password</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id="re-password"
                placeholder="Password"
                onChange={(e) => setPassword2(e.target.value)}
              />
              <label htmlFor="password">Re-enter Your Password</label>
              {/* conditional */}
              {failed ? (
                <div className="d-flex justify-self-start text-danger">
                  Passwords not the same.
                </div>
              ) : (
                ""
              )}
            </div>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleRegister}
            >
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
