import React, { useState, useEffect } from "react";
import { Modal, Button } from "react-bootstrap";
import { local_url } from "..";
import axios from "axios";

const ActivateTwoFA = (props) => {
  const [QR, setQR] = useState([]);
  const [OTP, setOTP] = useState("");
  const [success, setSuccess] = useState(0);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const user_id = localStorage.getItem("user_id");
        console.log(user_id);
        const response = await axios.get(`${local_url}two-fa`, {
          params: { user_id },
        });
        setQR(response.data.data);
        console.log("it is", QR.data_url);
      } catch (error) {
        console.log("error oh!!!");
        console.error(error);
      }
    };
    fetchData();
  }, []);

  const handleOTP = async () => {
    console.log(OTP);
    if (!OTP || OTP.length < 6) {
      return alert("All fields are required");
    }
    const otp = {
      otp: OTP,
    };
    try {
      const response = await axios.post(`${local_url}`, otp, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      console.log(response.status);
      if (response.status === 200) {
        setSuccess(1);
        alert("Logged in");
      }
    } catch (error) {
      if (error.response) {
        console.error("Error response:", error.response.data);
        alert(error.response.data.message);
      } else {
        console.error("Error message:", error.message);
      }
    }
    return;
  };

  return (
    <div>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Activate 2FA
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="text-center">
            <h4>
              With your authenticator app, scan the code and input OTP to
              activate 2FA
            </h4>
            <p>
              With your authenticator app, scan the code and input OTP to
              activate 2FA
            </p>
          </div>
          <div className="d-flex justify-content-center">
            <img className="img-fluid" src={QR.data_url} alt="" />
          </div>
          <div className="text-center" size={6}>
            <input
              type="number"
              onChange={(e) => {
                setOTP(e.target.value);
                // console.log(OTP);
              }}
            />
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleOTP}
            >
              Validate
            </button>
          </div>
          {/* <p></p> */}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ActivateTwoFA;
