import React, { useState, useEffect } from "react";
import { Modal, Button } from "react-bootstrap";
import { local_url } from "..";
import axios from "axios";

const ActivateTwoFA = (props) => {
  const [QR, setQR] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${local_url}two-fa`);
        setQR(response.data.data);
        console.log("it is", QR.data_url);
      } catch (error) {
        console.log("error oh!!!");
        console.error(error);
      }
    };
    fetchData();
  }, []);
  return (
    <div>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Activate 2FA
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>
            With your authenticator app, scan the code and input OTP to activate
            2FA
          </h4>
          <p>
            With your authenticator app, scan the code and input OTP to activate
            2FA
          </p>
          <div className="d-flex justify-content-center">
            <img className="img-fluid" src={QR.data_url} alt="" />
          </div>
          {/* <p></p> */}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ActivateTwoFA;
